import React from 'react';
import '../../styles/Portfolio.css'
import Icon1 from '../../images/travel.svg';
import Icon2 from '../../images/diet.svg';
import Icon3 from '../../images/movie.svg';
import {
  PortfolioContainer,
  PortfolioH1,
  PortfolioWrapper,
  PortfolioCard,
  PortfolioIcon1,
  PortfolioIcon2,
  PortfolioIcon,
  PortfolioH2,
  PortfolioP
} from './PortfolioElements';
import Carousel from 'react-elastic-carousel';
import styled from "styled-components";

const Item = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 250px;
  width: 100%;
  background-color: #000;
  color: #fff;
  margin: 0 15px;
  font-size: 4em;
  z-index: 2;
`;

const CarouselContainer = styled.div`
  width: 90%;
  max-width: 1200px;
  z-index: 2;
  display: flex;

`;

const breakPoints = [
  { width: 1, itemsToShow: 1 },
  { width: 550, itemsToShow: 2 },
  { width: 768, itemsToShow: 3 },
  { width: 1200, itemsToShow: 4 },
];

const Portfolio = () => {
  return (
    <PortfolioContainer id='portfolio'>
      <PortfolioH1>Portfolio</PortfolioH1>
      <div class="line2"></div>

      <CarouselContainer>
      <Carousel breakPoints={breakPoints} className="carousel">
      {/* <PortfolioWrapper> */}
      <a href='https://remyduijn.github.io/Watch-it-Outside/' target="_blank" rel="noopener noreferrer">
        <PortfolioCard>
          <PortfolioIcon1 src={Icon3} />
          <PortfolioH2>Watch it Outside</PortfolioH2>
          <PortfolioP>
            A registration website for a film festival.
          </PortfolioP>
        </PortfolioCard></a>
        <a href='https://www.ikigaibrazil.com/' target="_blank" rel="noopener noreferrer">
        <PortfolioCard>
          <PortfolioIcon src={Icon1} />
          <PortfolioH2>Ikigai Brazil</PortfolioH2>
          <PortfolioP>
            A blog about travel and immigration in Brazil.
          </PortfolioP>
        </PortfolioCard></a>
        <a href='https://remyduijn.github.io/Nutrition-Needs/' target="_blank" rel="noopener noreferrer">
        <PortfolioCard>
          <PortfolioIcon2 src={Icon2} />
          <PortfolioH2>Nutrition Needs</PortfolioH2>
          <PortfolioP>
            A web program that calculates nutritional needs.
          </PortfolioP>
        </PortfolioCard></a>
        <a href='https://remyduijn.github.io/Watch-it-Outside/' target="_blank" rel="noopener noreferrer">
        <PortfolioCard>
          <PortfolioIcon1 src={Icon3} />
          <PortfolioH2>Watch it Outside</PortfolioH2>
          <PortfolioP>
            A registration website for a film festival.
          </PortfolioP>
        </PortfolioCard></a>
        <a href='https://www.ikigaibrazil.com/' target="_blank" rel="noopener noreferrer">
        <PortfolioCard>
          <PortfolioIcon src={Icon1} />
          <PortfolioH2>Ikigai Brazil</PortfolioH2>
          <PortfolioP>
            A blog about travel and immigration in Brazil.
          </PortfolioP>
        </PortfolioCard></a>
        <a href='https://remyduijn.github.io/Nutrition-Needs/' target="_blank" rel="noopener noreferrer">
        <PortfolioCard>
          <PortfolioIcon2 src={Icon2} />
          <PortfolioH2>Nutrition Needs</PortfolioH2>
          <PortfolioP>
            A web program that calculates nutritional needs.
          </PortfolioP>
        </PortfolioCard></a>
        {/* </PortfolioWrapper> */}
        </Carousel>
        </CarouselContainer>

    </PortfolioContainer>
  );
};

export default Portfolio;
